<?php/** * This is the model class for table "{{feedback}}". * * The followings are the available columns in table '{{feedback}}': * @property integer $id * @property string $name * @property string $tel * @property string $email * @property string $content * @property string $reply * @property integer $create_time * @property integer $update_time */class CmsFeedback extends JPDActiveRecord{	/**	 * Returns the static model of the specified AR class.	 * @return Feedback the static model class	 */	public static function model($className=__CLASS__)	{		return parent::model($className);	}	/**	 * @return string the associated database table name	 */	public function tableName()	{		return '{{cms_feedback}}';	}	/**	 * @return array validation rules for model attributes.	 */	public function rules()	{		// NOTE: you should only define rules for those attributes that		// will receive user inputs.		return array(			array('Name, Tel, Email, Content', 'required'),			array('CreateTime, UpdateTime', 'numerical', 'integerOnly'=>true),			array('Name', 'length', 'max'=>50),			array('Tel, Email', 'length', 'max'=>100),                        array('Reply', 'length', 'max'=>1000),			// The following rule is used by search().			// Please remove those attributes that should not be searched.			array('ID, Name, Tel, Email, Content, Reply, CreateTime, UpdateTime', 'safe', 'on'=>'search'),		);	}	/**	 * @return array relational rules.	 */	public function relations()	{		// NOTE: you may need to adjust the relation name and the related		// class name for the relations automatically generated below.		return array(		);	}	/**	 * @return array customized attribute labels (name=>label)	 */	public function attributeLabels()	{		return array(			'ID' => 'ID',			'Name' => '名字',			'Tel' => '电话',			'Email' => 'Email',			'Content' => '内容',			'Reply' => '回复',			'CreateTime' => '发布时间',			'UpdateTime' => '更新时间',		);	}	/**	 * Retrieves a list of models based on the current search/filter conditions.	 * @return CActiveDataProvider the data provider that can return the models based on the search/filter conditions.	 */	public function search()	{		// Warning: Please modify the following code to remove attributes that		// should not be searched.		$criteria=new CDbCriteria;		$criteria->compare('ID',$this->ID);		$criteria->compare('Name',$this->Name,true);		$criteria->compare('Tel',$this->Tel,true);		$criteria->compare('Email',$this->Email,true);		$criteria->compare('Content',$this->Content,true);		$criteria->compare('Reply',$this->Reply,true);		$criteria->compare('CreateTime',$this->CreateTime);		$criteria->compare('UpdateTime',$this->UpdateTime);		return new CActiveDataProvider(get_class($this), array(			'criteria'=>$criteria,		));	}       public function beforeSave() {        if (parent::beforeSave()) {            if ($this->isNewRecord) {                $this->CreateTime = $this->UpdateTime = time();            }            else                $this->UpdateTime = time();            return true;        }        else            return false;    }}